const rules = {
	require({label}) {
		return v => !!v || `[${label}] 필수 입력입니다.`;
	},
	min({label, len=3}){
		return v => !!v ? v.length >= len || `[${label}] ${len}자 이상 입력하세요.` : true;
	},
	Num() {
		return v => !!v ? /^[0-9_]+$/.test(v) || `숫자만 입력하세요.` : true; 
	},
	alphaNum() {
		return v => !!v ? /^[a-zA-Z0-9_]+$/.test(v) || `영어와 숫자만 입력하세요.` : true; 
	},	
	pattern({label, pattern}) {
		return v => !!v ? pattern.test(v) || `[${label}] 형식에 맞게 입력하세요.` : true; 
	},
	matchValue(origin) {
		return v => origin === v || '비밀번호가 일치하지 않습니다.';
	},
	id(options) {
		const defaultOptions = {
			label : '아이디',
			len : 3,
			required : true,
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.min(opt));
		arr.push(rules.alphaNum());
		return arr;
	},
	name(options) {
		const defaultOptions = {
			label : '이름',
			len : 2,
			required : true,
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.min(opt));
		return arr;
	},
	email(options) {
		const defaultOptions = {
			label : '이메일',
			required : true,
			pattern : /.+@.+\..+/
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.pattern(opt));
		return arr;
	},
	password(options) {
		const defaultOptions = {
			label : '비밀번호',
			required : true,
			len : 6,
			pattern : /^.*(?=^.{6,}$)(?=.*\d)(?=.*[a-zA-Z]).*$/
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.min(opt));
		arr.push(rules.pattern(opt));
		return arr;
	},
	password2(options) {
		const defaultOptions = {
			label : '비밀번호',
			required : true,
			len : 6,			
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.min(opt));
		return arr;
	},
	date(options) {
		const defaultOptions = {
			label : '날자',
			required : true,
			pattern : /^\d{4}-\d{2}-\d{2}$/
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.pattern(opt));
		return arr;
	},
	phone(options) {
		const defaultOptions = {
			label : '전화번호',
			required : true,
			pattern : /^(\d{2,3}-)?\d{3,4}-\d{4}$/
		};
		const opt = Object.assign(defaultOptions, options);
		const arr = [];
		if(opt.required) {
			arr.push(rules.require(opt));
		}
		arr.push(rules.pattern(opt));
		return arr;
	},
};

module.exports = rules;

