import Vue from 'vue'
import VueRouter from 'vue-router'
import routes from './routes';
import store from '../store';
import { LV, LV_LABEL } from '../../util/level';

Vue.use(VueRouter)

export function createRouter() {
	const router = new VueRouter({
		mode: 'history',
		base: process.env.BASE_URL,
		routes
	});

	router.beforeEach(async (to, from, next) => {
		const { $Progress, $toast } = Vue.prototype;

		if ($Progress) $Progress.start();

		if (typeof (window) == 'object') {
			if (!store.state.appReady) {
				if (window.__INITIAL_STATE__) {
					store.replaceState(window.__INITIAL_STATE__);
				} else {
					await store.dispatch('appInit');
				}
			}
		}

		const access = store.getters.access;
		const GRANT = store.getters['user/GRANT'];
		const isMember = !!store.state.user.member;

		let msg = '';
		if (to.name.startsWith('NoAuth') && isMember) {
			// 비회원 인경우에만 접근
			msg = "이미 로그인 되어 있습니다.";
		} else if (to.name.startsWith('Adm') && GRANT < LV.SUPER) {
			// 관리자 전용 페이지
			msg = `${LV_LABEL(LV.SUPER)}(${LV.SUPER}) 이상 접근 가능합니다.`;
		} else {
			// 메뉴 접근 레벨에 따름			
			const accessLV = access[to.path] || LV.BLOCK;
			if (accessLV > GRANT) {
				msg = `${LV_LABEL(accessLV)}(${accessLV}) 이상 접근 가능합니다.`;
			}
		}
		if (msg) {
			// 접근 차단
			if ($toast) $toast.error(msg);
			if ($Progress) $Progress.fail();
			if (from.name) { // 이전 경로가 있으면 라우팅을 동작하지 않고
				next(false);			
			} else { // 이전 경로가 없으면 홈으로 이동 또는 로그인 페이지로 보내도 됨
				return isMember ? next('/') : next({
					name: 'NoAuthLogin',
					query: { next: to.fullPath }
				});
			}
		} else {
			// 통과
			if ($Progress) $Progress.finish();
			next();
		}
	});

	router.afterEach((to, from) => {

	});
	return router;
}

import Vue from 'vue'
import App from './App.vue'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from "vuex-router-sync";

import plugins from './plugins';
import '@babel/polyfill'
import 'roboto-fontface/css/roboto/roboto-fontface.css'
import '@mdi/font/css/materialdesignicons.css'

import Mixins from './mixins';

Vue.config.productionTip = false

export function createApp(ctx) {
	const router = createRouter();
	const store = createStore();
	sync(store, router);

	const mixins = Object.keys(Mixins);
	for(const mixin of mixins) {
		Vue.mixin(Mixins[mixin]);
	}


	const app = new Vue({
		data: { url: ctx ? ctx.url : '' },
		router,
		store,
		vuetify : plugins.vuetify,
		render: h => h(App)
	});

	return { app, router, store };
}

import { createApp } from "./main";

export default (ctx) => {
	return new Promise( async (resolve, reject) => {
		const { app, router, store } = createApp(ctx);
		//  console.log('ctx', ctx);
		await store.dispatch('appInit', ctx);		
		router.push(ctx.url);		
		router.onReady(() => {			
			ctx.rendered = () => ctx.state = store.state;
			const matchedComponents = router.getMatchedComponents();
			if (matchedComponents.length === 0) {
				return reject({ code: 404 });
			}
			return resolve(app);
		}, reject);
	});
}

const level = {
	LV: {
		BLOCK: 0,
		AWAIT: 1,
		MEMBER: 2,		
		DELIVERY : 4,		
		MANAGER: 5,
		PRODUCTION: 6,
		BUSINESS: 7,
		ADMIN: 8,
		SYSTEM: 9,
		SUPER: 10
	},
	LVITEMS : [
		{ label: "BLOCK", lv: 0 },
		{ label: "AWAIT", lv: 1 },
		{ label: "MEMBER", lv: 2 },		
		{ label: "DELIVERY", lv: 4 },		
		{ label: "MANAGER", lv: 5 },
		{ label: "PRODUCTION", lv: 6 },
		{ label: "BUSINESS", lv: 7 },
		{ label: "ADMIN", lv: 8 },
		{ label: "SYSTEM", lv: 9 },
	],
	LV_LABEL: (lv) => {
		if (lv >= level.LV.SUPER) {
			return '프로그램관리자';
		} else if (level.LV.ADMIN <= lv && lv < level.LV.SYSTEM) {
			return '시스템관리자';
		} else if (level.LV.BUSINESS <= lv && lv < level.LV.ADMIN) {			
			return '관리자';
		} else if (level.LV.PRODUCTION <= lv && lv < level.LV.BUSINESS) {
			return '영업관리';
		} else if (level.LV.MANAGER <= lv && lv < level.LV.PRODUCTION) {
			return '생산계획';
		} else if (level.LV.DELIVERY <= lv && lv < level.LV.MANAGER) {
			return '생산관리';
		} else if (level.LV.MEMBER <= lv && lv < level.LV.DELIVERY) {
			return '납품관리';
		} else if (level.LV.AWAIT <= lv && lv < level.LV.MEMBER) {
			return '일반';
		} else if (level.LV.AWAIT == lv) {
			return '대기';
		} else {
			return '차단';
		}
	},
	LV_COLOR: (lv) => {
		if (lv >= level.LV.SUPER) {
			return 'light-blue accent-4';
		} else if (level.LV.ADMIN <= lv && lv < level.LV.SUPER) {
			return 'green lighten-1';
		} else if (level.LV.VIP <= lv && lv < level.LV.ADMIN) {
			return 'green lighten-2';
		} else if (level.LV.MEMBER <= lv && lv < level.LV.VIP) {
			return 'lime lighten-3';
		} else if (level.LV.AWAIT == lv) {
			return 'red accent-2';
		} else {
			return 'red accent-4';
		}
	},
	isGrant(req, lv) {
		return req.user && req.user.i_level >= lv ? true : false;
	}
};

module.exports = level;

